        [] xyz:resource ?resource ;
            xyz:copyright ?copyright ;
            xyz:licence ?licence ;
            xyz:terms ?terms .
    }

    BIND ( ! (?copyright = "" || ?copyright = "[]") AS ?copyright_h )
    BIND ( ! (?licence = "" || ?licence = "[]") AS ?licence_h )
    BIND ( ! (?terms = "" || ?terms = "[]") AS ?terms_h )
} 
group by ?resource

